UNSPLASH_ACCESS_KEY = 'yasNTDaq59oWHSC2A9QKt1koifDXO__uAAuXE33iqMs'

def fetch_unsplash_image(query, width=1920, height=600):
    url = f"https://api.unsplash.com/search/photos?query={query}&client_id={UNSPLASH_ACCESS_KEY}&w={width}&h={height}"
    response = requests.get(url)
    data = response.json()
    if data['results']:
        return data['results'][0]['urls']['regular']
    return None


